function PopupWithForm({ name, title, isOpen, children, onSubmit, buttonText, onClose }) {

    return (
        <div className={`popup ${props.isOpen ? 'popup_opened' : ''}`}>
            <div className="popup__container">
                <form className="popup__form" name={name} noValidate onSubmit={props.onSubmit}>
                    <h2 className="popup__title">{props.title}</h2>
                    {props.children}
                    <button type="submit" className="popup__button">Сохранить</button>
                </form>
                <button type="button" className="popup__close-button popup__close-button_profile-edit" onClick={props.onClose}></button>
            </div>
        </div>
    )
}

export default PopupWithForm
