import React from 'react'
// import PopupWithForm from './PopupWithForm'
import Form from './Form'

function EditAvatarPopup({ isOpen, onUpdateAvatar, onClose }) {

    const avatarRef = React.useRef('')

    function handleSubmit(e) {
        e.preventDefault()
        onUpdateAvatar({
            avatar: avatarRef.current.value
        })
    }


    return (
        <Form formName='profile-edit' onSubmit={handleSubmit} title='Редактировать профиль' buttonText='Сохранить' isModal={true} isOpen={isOpen}>
      <input
        name="userAvatar"
        type="url"
        className="popup__input popup__input_data_about"
        id="userAvatar-input"
        placeholder="Ссылка на картинку"
        required
        ref={avatarRef}
      />
      <span className="popup__input-error userAvatar-input-error"></span>
      <button
        type="button"
        className="popup__close-button"
        onClick={onClose}
      >
      </button>
    </Form>
        // <PopupWithForm name='avatar-edit' title='Обновить аватар' isOpen={props.isOpen} onClose={props.onClose} onSubmit={handleSubmit}>
        //     <input id="avatar-link" name="userAvatar" className="popup__input popup__input_place_description" type="url" placeholder="Ссылка на картинку" minLength="2" maxLength="100000" required ref={avatarRef}></input>
        //     <span id="avatar-link-error" className="popup__input-error"></span>
        // </PopupWithForm>
    )

}

export default EditAvatarPopup
